import React from 'react'
import { useState } from 'react'

const Todo = () => {

    var [data, setData] = useState('')
    var [list, setList] = useState([])

    const inEVe = (event) => {
        setData(event.target.value)
    }

    const addItem = () => {
        if (!data) {
            alert('Pleas add some data ')
        }
        else {
            setList((prevList) => {
                return [...prevList, data]
            })
            setData('')
        }
    }

    const remove = (event) => {
        const id = event.target.id

        const updatedata = list.filter( (element , index) => {
            return id !== index
        })
    }
    return (
        <>
            <div className='main-div'>
                <div className='child-div'>
                    <figure>
                        <img src='https://wallpapercave.com/wp/wp10309423.jpg' alt='' />
                        <figcaption>Add your list here</figcaption>
                    </figure>

                    <div className='addItems'>
                        <input type="text" placeholder='✍️ write here' onChange={inEVe} value={data} />
                        <i className="fa fa-plus add-btn" title='Add Item' onClick={addItem}></i>
                    </div>

                    <div className='showItems'>
                        {
                            list.map((currData, ind) => {
                                return (
                                    <>
                                        <div className='eachItem'>
                                            <h3 style={{textTransform : 'capitalize'}}>{currData}</h3>
                                            <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={remove} id={ind}></i>
                                        </div>
                                    </>
                                )
                            })
                        }
                    </div>

                    <div className='showItems'>
                        <button className='btn effect04' data-sm-link-text="Remove All"> <span>CHECK LIST</span></button>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Todo
