import React from 'react'
import { useState } from 'react'

const Todo = () => {

    var [data, setData] = useState('')
    var [list, setList] = useState([])

    const inEVe = (event) => {
        setData(event.target.value)
    }

    const addItem = () => {
        if (!data) {
            alert('Pleas add some data ')
        }
        else {
            const actData = {id : new Date().getTime.toString() , name : data};
            setList((prevList) => {
                return [...prevList, actData]
            })
            setData('')
        }
    }

    // const remove = (event) => {
    //     const id = event.target.id
    //     console.log('click');
    //     console.log(id);
    //     const updatedata = list.filter( (element , index) => {
    //         return id != index
    //     })
    //     setList(updatedata)
    // }
    
    // Above function is works fine but in big projects it is not preferable so we use below method 
    
    const remove = (event) => {
        const id = event.target.id
        console.log('click');
        console.log(id);
        const updatedata = list.filter( (element , index) => {
            return id != element.id
        })
        setList(updatedata)
    }
    const removeAll = () =>{
        setList([])
    }
    return (
        <>
            <div className='main-div'>
                <div className='child-div'>
                    <figure>
                        <img src='https://wallpapercave.com/wp/wp10309423.jpg' alt='' />
                        <figcaption>Add your list here</figcaption>
                    </figure>

                    <div className='addItems'>
                        <input type="text" placeholder='✍️ write here' onChange={inEVe} value={data} />
                        <i className="fa fa-plus add-btn" title='Add Item' onClick={addItem}></i>
                    </div>

                    <div className='showItems'>
                        {
                            list.map((currData, ind) => {
                                return (
                                    <>
                                        <div className='eachItem'>
                                            {/* <h3 style={{textTransform : 'capitalize'}}>{currData}</h3>
                                            <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={remove} id={ind}></i> */}

                                            {/* Above is for previous way  */}

                                            <h3 style={{textTransform : 'capitalize'}}>{currData.name}</h3>
                                            <i className='far fa-trash-alt add-btn' title='Delete Item' onClick={remove(currData.id)}></i>
                                        </div>
                                    </>
                                )
                            })
                        }
                    </div>

                    <div className='showItems'>
                        <button className='btn effect04' data-sm-link-text="Remove All" onClick={removeAll}> <span>CHECK LIST</span></button>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Todo
